import React, { Component } from "react";
import {
  View,
  Text,
  Alert,
  AsyncStorage,
  FlatList,
  TouchableOpacity,
  Image,
  TextInput,
  I18nManager,
  ActivityIndicator,
  ScrollView
} from "react-native";
import styles from "../Constants/Styles";
import AntIcon from "react-native-vector-icons/AntDesign";
import * as CONSTANT from "../Constants/Constant";
import {
  Collapse,
  CollapseHeader,
  CollapseBody
} from "accordion-collapse-react-native";
import DocumentPicker from "react-native-document-picker";
import ImagePicker from "react-native-image-crop-picker";
import axios from "axios";

class ProjectsProfile extends Component {
  state = {
    ProjectsData: [],
    Projectrefresh: false,
    isLoading: true,
    link:'',
    title:'',
    image:'',
    editLink:'',
    editTitle:'',
    editImage:{},
    updateProjectData: [],
    editProjectimages: {}
  };
  componentDidMount() {
    this.fetchProjectData();
  }
  fetchProjectData = async () => {
    const Uid = await AsyncStorage.getItem("projectUid");
    const response = await fetch(
      CONSTANT.BaseUrl + "profile/get_profile_tab_settings?user_id=" + Uid
    );
    const json = await response.json();
    if (
      Array.isArray(json) &&
      json[0] &&
      json[0].type &&
      json[0].type === "error"
    ) {
      this.setState({ ProjectsData: [], isLoading: false }); // empty data set
    } else {
      this.setState({ ProjectsData: json.projects, isLoading: false, updateProjectData: [] });
    }
    console.log('ProjectsData', this.state.ProjectsData)

    for(let u = 0; u < this.state.ProjectsData.length; u++ ){
      this.state.updateProjectData.push({
        title: this.state.ProjectsData[u].title,
        link: this.state.ProjectsData[u].link,
        image:{
          attachment_id: this.state.ProjectsData[u].image.attachment_id.toString(),
          url: this.state.ProjectsData[u].image.url
        },
        // image0: {}
      });
    }
    console.log('updateProjectData', this.state.updateProjectData)
  };
  AddNowClicked = () => {
    this.state.ProjectsData.push({
      title: this.state.title,
      link: this.state.link,
      image:{
        attachment_id: '',
        url: this.state.image.uri
      },
    });
    console.log(this.state.title);
    console.log(this.state.link);
    console.log(this.state.image.url);
    console.log('final arrey', this.state.ProjectsData);
    this.setState({
      Projectrefresh: true,
      title:'',
      link:'',
      image: ''
    });

    this.state.updateProjectData.push({
      title: this.state.title,
      link: this.state.link,
      image0: this.state.Projectimages,
    });
    console.log('update final arrey', this.state.updateProjectData);
  };
  EditData = (index , item, image) => {
    console.log('item 123', item.image.attachment_id)
    console.log('this.state.editImage.uri', this.state.editImage.uri)

    this.state.ProjectsData[index] = {
      title: this.state.editTitle != '' ? this.state.editTitle : item.title, 
      link: this.state.editLink != '' ? this.state.editLink : item.link, 
      image:{
        attachment_id: item.image.attachment_id,
        url: this.state.editImage.uri != undefined ? this.state.editImage.uri : image
      },
      //url: this.state.editImage.uri != undefined ? this.state.editImage.uri : image
    }
    this.setState({Projectrefresh: true, editImage: {}})
    console.log('1st edit arrey', this.state.ProjectsData)
    this.state.updateProjectData[index] = {
      title: this.state.editTitle != '' ? this.state.editTitle : item.title, 
      link: this.state.editLink != '' ? this.state.editLink : item.link,
      image:{
        attachment_id: item.image.attachment_id,
        url: image
      },
      image0: this.state.editProjectimages,
    };
    console.log('2nd edit arrey', this.state.updateProjectData)
  };
  onDeletePress = index => {
    this.state.ProjectsData.splice(index, 1);
    this.state.updateProjectData.splice(index, 1);
    this.setState({
      Projectrefresh: true
    });
  };

  // pickSingleImage() {
  //   ImagePicker.openPicker({
  //   })
  //     .then(image => {
  //       this.setState({
  //         image: image.path.toString(),
  //       });
  //       console.log(image.path);
  //     })
  //     .catch(e => console.log(e));
  // }
  pickSingleImage() {
    ImagePicker.openPicker({
      width: 300,
      height: 300,
      cropping: true,
      includeBase64: true,
    })
      .then(image => {
        this.setState({
          image: {
            uri: `data:${image.mime};base64,` + image.data,
            width: image.width,
            height: image.height,
          },
        });
        this.setState({
          Projectimages: {
            name: Platform.OS === 'ios' ? image.filename : image.path.slice(76),
            type: image.mime,
            base64_string: image.data,
          },
        });
        console.log('image', image)
        console.log('Projectimages', this.state.Projectimages)
      })
      .catch(e => console.log(e));
  }
  editPickProjects() {
    ImagePicker.openPicker({
      width: 300,
      height: 300,
      cropping: true,
      includeBase64: true,
      includeExif: true,
    })
      .then(image => {
        this.setState({
          editImage: {
            uri: `data:${image.mime};base64,` + image.data,
          },
        });
        console.log('editImage', this.state.editImage)
        this.setState({
          editProjectimages: {
            name: Platform.OS === 'ios' ? image.filename : image.path.slice(76),
            type: image.mime,
            base64_string: image.data,
          },
        });
      })
      .catch(e => console.log(e));
  }
  UpdateProfileData = async () => {
    this.setState({ isLoading: true });
    const {
      updateProjectData,
    } = this.state;
    const Uid = await AsyncStorage.getItem('projectUid');
    axios
      .post(CONSTANT.BaseUrl + "profile/update_tabe_settings?user_id=" + Uid + "&edit_type=" + "projects",{
        project: updateProjectData,
      })
      .then(async response => {
        if (response.status === 200) {
          this.setState({ isLoading: false });
          Alert.alert(CONSTANT.Success, response.data.message);
          console.log("Success", response.data.message)
          this.fetchProjectData();
        } else if (response.status === 203) {
          this.setState({ isLoading: false });
          Alert.alert(CONSTANT.Error, response.data.message);
          console.log("Error", response.data.message)
        }
      })
      .catch(error => {
        Alert.alert(error);
        console.log(error);
      });
  };

  render() {
    const { isLoading } = this.state;
    return (
      <View style={styles.container}>
        {isLoading ? (
          <View style={styles.ActivityIndicatorAreaStyle}>
            <ActivityIndicator
              size="small"
              color={CONSTANT.primaryColor}
              style={styles.ActivityIndicatorStyle}
            />
          </View>
        ) : null}
        <ScrollView>
        <View style={styles.PersonalDetailSectionsStyle}>
          <View style={styles.PersonalDetailSectionArea}>
            <View
              style={{
                height: 50,
                justifyContent: 'center',
                paddingHorizontal:10,
                marginVertical:10,
                backgroundColor: "#fcfcfc",
                borderLeftWidth: 5,
                borderLeftColor: CONSTANT.primaryColor,
              }}
            >
              <Text style={styles.MainHeadingTextStyle}>{CONSTANT.ProjectProfileAddProjects}</Text>
            </View>
            <TextInput
              underlineColorAndroid="transparent"
              style={styles.TextInputLayoutStyle}
              name="Project Title"
              placeholder={CONSTANT.ProjectProfileProjectTitle}
              placeholderTextColor="#807f7f"
              onChangeText={title => this.setState({ title })}
              value={this.state.title}
            />
            <TextInput
              underlineColorAndroid="transparent"
              style={styles.TextInputLayoutStyle}
              name="Project URL"
              placeholder={CONSTANT.ProjectProfileProjectURL}
              placeholderTextColor="#807f7f"
              onChangeText={link => this.setState({ link })}
              value={this.state.link}
            />
            <View style={{flexDirection:'row', height: 150, width:'100%'}}>
              <TouchableOpacity
                onPress={() => this.pickSingleImage()}
                style={styles.PersonalDetailDownloadArea}
              >
                <AntIcon name="plus" color={"#767676"} size={27} />
                <Text style={styles.PersonalDetailDownloadText}>
                  {CONSTANT.ProjectProfileAddImage}
                </Text>
              </TouchableOpacity>
              <Image
                resizeMode={'cover'}
                style={[styles.AwardandProjectImageStyle, {height:'100%', width:'50%'}]}
                source={{
                  uri:
                    this.state.image.uri == null || '' ?
                    "data:image/jpeg;base64,"
                    :
                    this.state.image.uri
                }}
              />
            </View>
            <TouchableOpacity
              onPress={this.AddNowClicked}
              style={[styles.MainButtonArea, {alignSelf:'flex-start'}]}>
              <Text style={styles.ButtonText}>
                {CONSTANT.ProfileAdd}
              </Text>
            </TouchableOpacity>
          </View>
          {this.state.ProjectsData.length >= 1 && (
            <FlatList
              showsVerticalScrollIndicator={false}
              data={this.state.ProjectsData}
              extraData={this.state.awardrefresh}
              keyExtractor={(y, z) => z.toString()}
              renderItem={({ item, index }) => (
                <View>
                  <Collapse>
                    <CollapseHeader>
                      <View
                        style={[
                          {
                            marginHorizontal:10,
                            paddingLeft: 10,
                            paddingRight: 10,
                            flexDirection: "row",
                            justifyContent: "space-between",
                          },
                          styles.AwardandProjectMainArea,
                          styles.Elevation
                        ]}
                      >
                        <View
                          style={{
                            flexDirection: "row",
                            justifyContent: "center",
                            alignContent: "center"
                          }}
                        >
                          <Image
                              resizeMode={'contain'}
                              style={styles.AwardandProjectImageStyle}
                              source={{
                                uri:
                                    item.image == undefined ?
                                    "data:image/jpeg;base64,"
                                    :
                                    item.image.url
                              }}
                            />
                          <View
                            style={{
                              flexDirection: "column",
                              justifyContent: "center"
                            }}
                          >
                            <Text style={styles.NameTextStyle}>{item.title}</Text>
                            <Text style={styles.ParagraphTextStyle}>
                              {item.link}
                            </Text>
                          </View>
                        </View>
                        <View style={{ flexDirection: "row" }}>
                          {/* <TouchableOpacity
                        style={{
                          backgroundColor: "#3d4461",
                          padding: 10,
                          height:60,
                          justifyContent: "center",
                          alignItems: "center"
                        }}
                        
                      >
                        <AntIcon name="edit" color={"#fff"} size={20} />
                      </TouchableOpacity> */}
                          <TouchableOpacity
                            onPress={() => this.onDeletePress(index)}
                            style={{
                              backgroundColor: "#ff5851",
                              padding: 10,
                              justifyContent: "center",
                              alignItems: "center"
                            }}
                          >
                            <AntIcon name="delete" color={"#fff"} size={20} />
                          </TouchableOpacity>
                        </View>
                      </View>
                    </CollapseHeader>
                    <CollapseBody>
                      <View style={styles.PersonalDetailCollapseBodyArea}>
                        <TextInput
                          underlineColorAndroid="transparent"
                          defaultValue={item.title}
                          style={styles.TextInputLayoutStyle}
                          name="Project Title"
                          placeholder={CONSTANT.ProjectProfileProjectTitle}
                          placeholderTextColor="#807f7f"
                          onChangeText={editTitle => this.setState({ editTitle })}
                        />
                        <TextInput
                          underlineColorAndroid="transparent"
                          defaultValue={item.link}
                          style={styles.TextInputLayoutStyle}
                          name="Project URL"
                          placeholder={CONSTANT.ProjectProfileProjectURL}
                          placeholderTextColor="#807f7f"
                          onChangeText={editLink => this.setState({ editLink })}
                        />
                        <View style={{flexDirection:'row', height: 150, width:'100%'}}>
                          <TouchableOpacity
                            onPress={() => this.editPickProjects()}
                            style={styles.PersonalDetailDownloadArea}
                          >
                            <AntIcon name="plus" color={"#767676"} size={27} />
                            <Text style={styles.PersonalDetailDownloadText}>
                              {CONSTANT.ProjectProfileAddImage}
                            </Text>
                          </TouchableOpacity>
                          <Image
                            resizeMode={'cover'}
                            style={[styles.AwardandProjectImageStyle, {height:'100%', width:'50%'}]}
                            source={{
                              uri:
                                this.state.editImage.uri == null || '' ?
                                "data:image/jpeg;base64,"
                                :
                                this.state.editImage.uri
                            }}
                          />
                        </View>
                        <TouchableOpacity
                          onPress={() => this.EditData(index, item, item.image.url)}
                          style={[styles.MainButtonArea, {alignSelf:'flex-start'}]}>
                          <Text style={styles.ButtonText}>
                            {CONSTANT.ProfileAdd}
                          </Text>
                        </TouchableOpacity>
                      </View>
                    </CollapseBody>
                  </Collapse>
                </View>
              )}
            />
          )}
        </View>
        <TouchableOpacity
						onPress={this.UpdateProfileData}
						style={styles.MainButtonArea}>
						<Text style={styles.ButtonText}>
              {CONSTANT.ProfileSave}
						</Text>
					</TouchableOpacity>
        </ScrollView>
      </View>
    );
  }
}

export default ProjectsProfile;
