import React, { Component } from "react";
import {
  View,
  Text,
  StyleSheet,
  FlatList,
  TouchableOpacity,
  ActivityIndicator,
  AsyncStorage,
  Image,
  ImageBackground,
  Alert
} from "react-native";
import { Header } from "react-native-elements";
import PostedServicesCard from "./PostedServicesCard";
import CustomHeader from "../Header/CustomHeader";
import { NavigationEvents } from 'react-navigation';
import AntIcon from "react-native-vector-icons/AntDesign";
import axios from "axios";
import * as CONSTANT from '../Constants/Constant';
import styles from '../Constants/Styles';
const Entities = require("html-entities").XmlEntities;
const entities = new Entities();
class PostedServices extends Component {
  constructor() {
    super();
    this.onEndReachedCalledDuringMomentum = true;
    this.state = {
    data: [],
    isLoading: true,
    Toataldata: "",
    page: 1,
    fetching_from_server: false,
    fetchServices:[]
  };
  this.offset = 1;
}
  componentDidMount() {
    this.fetchPostedServices();
  }
  fetchPostedServices = async () => {
    const Pid = await AsyncStorage.getItem("projectUid");
    const response = await fetch(
      CONSTANT.BaseUrl + "dashboard/get_services?user_id=" + Pid
    );
    const json = await response.json();
    if (Array.isArray(json) && json[0] && json[0].type && json[0].type === 'error') {
      this.setState({ fetchServices: [] }); // empty data set 
    } else {
      this.setState({ fetchServices: json  , isLoading:false});
    }
  };
 
  renderFooter() {
    return (
      //Footer View with Load More button
      <View>
        {this.state.Toataldata.toString() != this.state.data.length ? (
          <View style={styles.footer}>
            <TouchableOpacity
              onPress={this.loadMoreData}
              style={styles.loadMoreBtn}
            >
              <Text style={styles.loadMorebtnText}>{CONSTANT.LoadMore}</Text>
              {this.state.fetching_from_server ? (
                <ActivityIndicator color="white" style={{ marginLeft: 8 }} />
              ) : null}
            </TouchableOpacity>
          </View>
        ) : null}
      </View>
    );
  }

  deleteServiceAlert = (ID) =>  {
    Alert.alert(
      "Delete service",
      "Are you sure you want to delete your service?",
      [
        {
          text: "Cancel",
          onPress: () => console.log("Cancel Pressed"),
          style: "cancel",
        },
        { text: "OK", onPress: () => this.deleteService(ID) },
      ],
      { cancelable: false }
    );
  }
  deleteService = async (ID) => {
    this.setState({ isLoading: true})
    const Uid = await AsyncStorage.getItem('projectUid');
    axios
      .post(CONSTANT.BaseUrl + 'services/delete_service?user_id=' + Uid, {
        id: ID
      })
      .then(async response => {
        console.log(response);
        if (response.status === 200) {
          this.setState({ isLoading: false });
          Alert.alert(CONSTANT.Success, response.data[0].message);
          console.log("Success", response.data[0].message)
          this.fetchPostedServices();
        } else if (response.status === 203) {
          this.setState({ isLoading: false });
          Alert.alert(CONSTANT.Error, response.data[0].message);
          console.log("Error", response.data[0].message)
        }
      })
      .catch(error => {
        alert(error);
        console.log(error);
      });
  };

  render() {
    const { isLoading } = this.state;
    return (
      <View style={styles.container}>
       
       <View
          style={{
            height: 60,
            paddingLeft: 15,
            paddingRight: 15,
            width: '100%',
            backgroundColor: CONSTANT.primaryColor,
            flexDirection: 'row',
            shadowOffset: {width: 0, height: 2},
            shadowColor: '#000',
            shadowOpacity: 0.2,
            shadowRadius: 2,
            elevation: 10,
          }}>
          <TouchableOpacity
            onPress={() => this.props.navigation.goBack(null)}
            style={{
              flexDirection: 'column',
              width: '20%',
              display: 'flex',
              alignContent: 'center',
              alignSelf: 'center',
              justifyContent: 'center',
            }}>
            <AntIcon name="back" size={25} color={'#fff'} />
          </TouchableOpacity>
          <View
            style={{
              flexDirection: 'column',
              width: '60%',
              display: 'flex',
              alignContent: 'center',
              alignSelf: 'center',
              justifyContent: 'center',
            }}>
            <View
              style={{
                flexDirection: 'row',
                display: 'flex',
                alignSelf: 'center',
              }}>
              <Text
                style={{
                  fontSize: 18,
                  fontWeight: '500',
                  color: '#fff',
                  height: 30,
                  marginTop: 9,
                }}>
                {CONSTANT.PostedServicesPostedServices}
              </Text>
            </View>
          </View>
        </View>
        {isLoading && (
          <View style={{ justifyContent: "center", height: "100%" }}>
            <ActivityIndicator
              size="small"
              color={CONSTANT.primaryColor}
              style={{
                height: 30,
                width: 30,
                borderRadius: 60,
                alignContent: "center",
                alignSelf: "center",
                justifyContent: "center",
                backgroundColor: "#fff",
                elevation: 5
              }}
            />
          </View>
        )}
        {
          this.state.data != "" &&
          <View>
            <Text style={{fontSize:18 , margin:10 , fontWeight:'700'}}>{this.state.data[0].count_totals} {CONSTANT.CompletedServicesEmployersFound}</Text>
          </View>
        }
        {
          this.state.fetchServices.length >= 1 ?
          <FlatList
            data={this.state.fetchServices}
            keyExtractor={(x, i) => i.toString()}
            numColumns={2}
            renderItem={({ item }) => (
            <TouchableOpacity style={{
              flex: 1,
              flexDirection: "column",
              marginHorizontal: 10,
              marginVertical: 5,
              backgroundColor: "#fff",
              borderRadius: 5,
              elevation: 3,
              shadowOffset: { width: 0, height: 2 },
              shadowColor: "#000",
              shadowOpacity: 0.2,
            }}
                activeOpacity={0.9}
                onPress={() =>
                  this.props.navigation.navigate(
                    "DetailServiceScreen",
                    { service_id: item.ID }
                  )
                }
              >
              {/* <PostedServicesCard
                  //  imageUri_banner={{ uri: `${item.images.length >= 1 ? item.featured_img : 'data:image/jpeg;base64,'}` }}
                    imageUri_profile={{ uri: `${item.featured_img != "" ? item.featured_img : 'data:image/jpeg;base64,'}` }}
                    service_name={`${entities.decode(item.post_status)}`}
                    service_title={`${entities.decode(item.service_title)}`}
                    service_price={`${entities.decode(item.price)}`}
                    service_rating={item.queu_services}
              /> */}
              <View style={{ backgroundColor: "#000", borderTopRightRadius: 4 , borderTopLeftRadius: 4}}>
                <ImageBackground
                  style={{ height: 120, opacity: 0.6, zIndex: 1, borderTopRightRadius: 4, borderTopLeftRadius: 4}}
                  source={{ uri: `${item.featured_img != "" ? item.featured_img : 'data:image/jpeg;base64,'}` }}
                ></ImageBackground>
              </View>
              {/* <Image
                        style={{ height: 40, marginLeft: 10, width: 40, borderRadius: 20, borderColor: '#fff', borderWidth: 2, marginTop: -20, zIndex: 1 }}
                        source={this.props.imageUri_profile}
                      /> */}
              <View style={{ margin: 10 }}>
                <View style={{ flexDirection: "row" }}>
                  <AntIcon name="check" color={"#0ed200"} size={12} />
                  <Text style={{ fontSize: 10, marginLeft: 2 }}>
                    {`${entities.decode(item.post_status)}`}
                  </Text>
                </View>
                <Text numberOfLines={1} style={{ color: "#323232", fontSize: 14 }}>
                  {`${entities.decode(item.service_title)}`}
                </Text>
                <View style={{ flexDirection: "row", marginTop: 5 }}>
                  <Text style={{ fontWeight: "700", color: "#fe736e", fontSize: 14 }}>
                    {`${entities.decode(item.price)}`}
                  </Text>
                  <Text
                    style={{ fontSize: 10, marginLeft: 3, alignSelf: "flex-end" }}
                  >
                    {CONSTANT.ManageServicesStartingfrom}
                  </Text>
                </View>
                <View style={{ flexDirection: "row" }}>
                  <AntIcon name="star" color={"#FECB02"} size={12} />
                  <Text style={{ fontSize: 10, marginLeft: 2 }}>
                    {item.queu_services}/5
                  </Text>
                  <Text style={{ fontSize: 10, textAlign: "right" }}>
                    {" "}
                    {CONSTANT.PostedServicesinqueue}
                  </Text>
                </View>
                <View
                  style={{
                    flexDirection: "row",
                    width: "100%",
                    justifyContent: "center",
                    marginTop: 10,
                  }}
                >
                  {/* <View style={{width:"35%" , alignItems:'center' , justifyContent:'center' , backgroundColor:'green'  , borderRadius:5 , marginHorizontal:5 , padding: 5, justifyContent:"center"}}>
                            <AntIcon
                            name="edit"
                            color={"#fff"}
                            size={15} />
                            </View> */}

                  <TouchableOpacity
                    onPress={() => this.deleteServiceAlert(JSON.stringify(item.ID))}
                    style={{
                      width: 50,
                      alignItems: "center",
                      justifyContent: "center",
                      backgroundColor: CONSTANT.TextColorRed,
                      borderRadius: 5,
                      marginHorizontal: 5,
                      padding: 5,
                      justifyContent: "center",
                    }}
                  >
                    <AntIcon name="delete" color={"#fff"} size={15} />
                  </TouchableOpacity>
                </View>
              </View>
            </TouchableOpacity>
          )}
        //   ListFooterComponent={this.renderFooter.bind(this)}
        />
          :
          <View style={styles.NoDataMainArea}>
            <Image
              resizeMode={'contain'}
              style={styles.NoDataImageStyle}
              source={require('../Images/nodata.png')}
            />
          </View>
        }
        
      </View>
    );
  }
}
export default PostedServices;
